#     print(c)
# for idx1, i in enumerate(s):
#     c= s[:idx1+1]
#     print(c)
words = []
for idx1, i in enumerate(s):
    c= s[idx1:]
    for idx2, j in enumerate(c):
        w= c[:idx2+1]
        words.append(w)
# kevinWords = []
# stuartWords = []
kevin = {}
stuart = {}
for i in words:
    if i[0] in ['A','E','I','O','U']:
        # kevinWords.append(i)
        if i in kevin:
            kevin[i] += 1
        else:
            kevin[i] = 1
    else:
        # stuartWords.append(i)
        if i in stuart:
            stuart[i] += 1
        else:
            stuart[i] = 1
# print(kevinWords)
name1 = 'Kevin'
points1 = sum(list(kevin.values()))
# print(stuartWords)
name2 = 'Stuart'
points2 = sum(list(stuart.values()))

if points1>points2: print(name1,points1)
elif points1<points2: print(name2,points2)
else: print("Draw")